<?php $mailto = "mathnews5@gmail.com"; $subject = "Feedback form"; $message = "Values submitted from web site form:"; $header = "From: ".$_POST['email']; foreach ($_POST as $key => $value){    if (!is_array($value))   {       $message .= "\n".$key." : ".$value;    }    else   {       foreach ($_POST[$key] as $itemvalue)      {          $message .= "\n".$key." : ".$itemvalue;       }    } } mail($mailto, $subject, stripslashes($message), $header); ?>
